class Carts extends React.Component {
    state={
        Products:[
            {id:1,name:"short",count:2,imgurl:"./imgs/01.jpg",isInCart:true},
            {id:1,name:"jeans",count:1,imgurl:"./imgs/02.jpg",isInCart:true},
            {id:1,name:"boot",count:4,imgurl:"./imgs/03.jpg",isInCart:true},
            {id:1,name:"cloves",count:7,imgurl:"./imgs/04.jpg",isInCart:true},
            {id:1,name:"sweat",count:3,imgurl:"./imgs/05.jpg",isInCart:true}

        ]
    };
    handleSet = (Product) => {
        const products = [...this.state.details];
        const index = products.indexOf(Product);
        products[index] = { ...products[index] };
    }
    render() { 

        return (
            <Switch>
                <h1>Shopping Card</h1>
                {this.state.details.map}

            </Switch>

        );
    }
}

export default Carts;
