import React from "react";
import {Route , Switch,Redirect} from "react-router-dom";
import Home from './Home/Home';
import NotFound from './NotFound/NotFound';
import NavBar from "./NavBar/NavBar";
import Carts from './Carts/Carts';
import Login from './Login/Login';





class App extends React.Component {

    state={
        details:[
            {id:1,name:"short",count:2,imgUrl:"./imgs/01.jpg",isInCart:true},
            {id:2,name:"jeans",count:1,imgUrl:"./imgs/02.jpg",isInCart:true},
            {id:3,name:"boot",count:4,imgUrl:"./imgs/03.jpg",isInCart:true},
            {id:4,name:"cloves",count:7,imgUrl:"./imgs/04.jpg",isInCart:true},
            {id:5,name:"sweat",count:3,imgUrl:"./imgs/05.jpg",isInCart:true}

        ]
    };
    handleSet = (Product) => {
        const products = [...this.state.details];
        const index = products.indexOf(Product);
        products[index] = { ...products[index] };
        //Edit
        products[index].count++;
        //set
        this.setState({details:products});
    };
    discountHandler = (Product) => {
        const products = [...this.state.details];
        const index = products.indexOf(Product);
        products[index] = { ...products[index] };
        //Edit

        if(products[index].count > 0){
            products[index].count--;
        } else{}
        //set
        this.setState({details:products});
    };






    handleDelete = (product)=>{
        const newproduct = this.state.details.filter(p => p.id !== product.id);
        this.setState({ details:newproduct });
    };


    handelReset = () => {
        let products = [...this.state.details];

        products.map(p => {
            p.count = 0;
            return p;
        });
        this.setState({details:products})
    };

    render() { 
        return(
            <React.Fragment>
                <NavBar></NavBar>

                <main className="container">
                        <Switch>
                        <Route path='/home' component={Home}/>
                        <Route path='/carts' render={(props) => <Carts
                        details={this.state.details.filter((p) => p.isInCart)}
                        onIncrement={this.handleSet}
                        onDelete={this.handleDelete}
                        onReset={this.handelReset}
                        onDis={this.discountHandler}
                        {...props}
                        />
                        }/>
                            <Route path='/login' component={Login}/>

                            <Route path='/notfound' component={NotFound}/>
                            <Redirect to='/notfound'/>
                        </Switch>
                    </main>
            </React.Fragment>
        )
    }
}

export default App;
