import React from "react";
//import {Link} from 'react-router-dom';
import Products from '../Products/Products';


class Carts extends React.Component {
    state={
        Products:[
            {id:1,name:"short",count:2,imgurl:"./imgs/01.jpg",isInCart:true},
            {id:2,name:"jeans",count:1,imgurl:"./imgs/02.jpg",isInCart:true},
            {id:3,name:"boot",count:4,imgurl:"./imgs/03.jpg",isInCart:true},
            {id:4,name:"cloves",count:7,imgurl:"./imgs/04.jpg",isInCart:true},
            {id:5,name:"sweat",count:3,imgurl:"./imgs/05.jpg",isInCart:true}

        ]
    };
    handleSet = (Product) => {
        const products = [...this.state.Products];
        const index = products.indexOf(Product);
        products[index] = { ...products[index] };
    };
    handleDelete = (product)=>{
        const newproduct = this.state.Products.filter(p =>p.id !== product.id);
        this.setState({ Products:newproduct });
    }




    render() { 

        return (

            <React.Fragment>
                 <h1>Shopping Card</h1>
                {this.state.Products.map(p =>(
                    <Products
                    key={p.id}
                    product={p}
                    onDelete={this.handleDelete}>{p.name}</Products>
                ))}
            </React.Fragment>

        );
    }
}

export default Carts;
